import React, { FC, useState } from "react";
import IngredientList from "../ingredients/IngredientList/IngredientList";
import { IngredientProps } from "../ingredients/Ingredient/Ingredient";

interface LasagnaRecipeProps {}

const ingredients: IngredientProps[] = [
  { quantity: 3, unit: "large carrots,", description: "chopped" },
  { quantity: 1, unit: "red bell pepper,", description: "chopped" },
  { quantity: 1, unit: "medium zucchini,", description: "chopped" },
  { quantity: 1, unit: "medium yellow onion", description: "chopped" },
  { quantity: 6, unit: "oz", description: "baby spinach" },
  { quantity: 9, unit: "lasagna noodles", description: "" },
];

const LasagnaRecipe: FC<LasagnaRecipeProps> = () => {
  const [multiplier, setMultiplier] = useState(1);

  return (
    <div data-testid="LasagnaRecipe">
      <h1>Vegetable Lasagna</h1>
      <h3>So good it has an aura.</h3>
      <h4>Ingredients</h4>
      <button>1x</button>
      <button>2x</button>
      <button>3x</button>
      <IngredientList
        ingredients={ingredients}
        multiplier={multiplier}
      ></IngredientList>
    </div>
  );
};

export default LasagnaRecipe;
