import React, { FC, useState } from "react";
import Ingredient, { IngredientProps } from "../Ingredient/Ingredient";

interface IngredientListProps {
  multiplier?: number;
  ingredients: IngredientProps[];
}

const IngredientList: FC<IngredientListProps> = ({ ingredients }) => {
  const [multiplier, setMultiplier] = useState(1);

  function handleMultiplierButtonClick(m: number) {
    setMultiplier(m);
  }

  return (
    <div>
      <button onClick={() => handleMultiplierButtonClick(1)}>1x</button>
      <button onClick={() => handleMultiplierButtonClick(2)}>2x</button>
      <button onClick={() => handleMultiplierButtonClick(3)}>3x</button>
      <table data-testid="IngredientList">
        <tr>
          <th style={{ paddingRight: "8px" }}>Quantity</th>
          <th>Ingredient</th>
        </tr>
        {ingredients.map((ingredient) => (
          <Ingredient
            quantity={ingredient.quantity * multiplier}
            unit={ingredient.unit}
            description={ingredient.description}
          ></Ingredient>
        ))}
      </table>
    </div>
  );
};

export default IngredientList;
